function convert(){hr=15*parseInt($("#hr_input").val()),min=.25*parseFloat($("#min_input").val()),sec=parseFloat($("#sec_input").val())*(15/3600),dms=hr+min+sec,d=Math.trunc(dms),m=60*(dms-d),m_remain=m-Math.trunc(m),s=60*m_remain,m<0&&(m=-m),s<0&&(s=-s),sep=d+"° "+Math.trunc(m)+"′ "+s.toFixed(3)+"″",$("#dd__value").html(dms),$("#hms__value").html(sep)}